import React, { useState } from "react";
import { validateLoginForm, validateRegistrationForm } from "./validation";
import { useNavigate } from "react-router-dom";
import Popup from "./popupComponent";
import { useDispatch } from "react-redux";
import { login } from "../features/auth/authSlice";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [registerEmail, setRegisterEmail] = useState("");
  const [registerPassword, setRegisterPassword] = useState("");
  const [registerName, setRegisterName] = useState("");
  const [registerAddress, setRegisterAddress] = useState("");
  const [registerContactNumber, setRegisterContactNumber] = useState("");
  const [forgotPasswordEmail, setForgotPasswordEmail] = useState("");
  const [errors, setErrors] = useState({});
  const [activeForm, setActiveForm] = useState("login");
  const [popupMessage, setPopupMessage] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const switchToForm = (formName) => {
    setActiveForm(formName);
    setErrors({});
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    const validationErrors = validateLoginForm(email, password);
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        const { token, user } = data;

        dispatch(login({ token, role: user.role }));

        localStorage.setItem("authToken", token);
        localStorage.setItem("user", user.role);

        navigate("/game");

        setPopupMessage("User logged in successfully");
      } else if (response.status === 401 || response.status === 400) {
        setErrors({ email: "Invalid email or password" });
      } else {
        console.error("Error logging in:", data.message);
        setErrors({ email: data.message });
      }
    } catch (error) {
      console.error("Server error:", error);
      setErrors({ email: "Server error" });
    } finally {
      setTimeout(() => {
        setPopupMessage("");
      }, 3000);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const validationErrors = validateRegistrationForm(
      registerEmail,
      registerPassword,
      registerName,
      registerAddress,
      registerContactNumber
    );
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name: registerName,
          email: registerEmail,
          password: registerPassword,
          address: registerAddress,
          contact: registerContactNumber,
        }),
      });

      const data = await response.json();
      if (response.status === 201) {
        setPopupMessage("User registered successfully");
        setTimeout(() => {
          setPopupMessage("");
          switchToForm("login");
        }, 3000);
      } else {
        console.error("Error registering user:", data.message);
        setErrors({ registerEmail: data.message });
      }
    } catch (error) {
      console.error("Server error:", error);
      setErrors({ registerEmail: "Server error" });
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    console.log("Forgot password for:", forgotPasswordEmail);
    setPopupMessage("Password reset link sent to your email");
    setTimeout(() => {
      setPopupMessage("");
      switchToForm("login");
    }, 3000);
  };

  return (
    <>
      <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-r from-white to-green-200">
        <div className="w-full md:w-1/2 p-8 flex justify-center">
          <div className="w-full max-w-sm">
            {activeForm === "login" && (
              <form
                onSubmit={handleLogin}
                className="bg-white shadow-md rounded-lg px-8 pt-6 pb-8 mb-4 text-center"
              >
                <h2 className="text-2xl font-semibold mb-4 text-gray-800">
                  Login
                </h2>
                <div className="mb-4">
                  <label htmlFor="email" className="block mb-1 text-gray-700">
                    Email
                  </label>
                  <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className={`w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500 ${
                      errors.email ? "border-red-500" : ""
                    }`}
                    placeholder="Enter your email"
                    required
                  />
                  {errors.email && (
                    <p className="text-red-500 text-sm mt-1">{errors.email}</p>
                  )}
                </div>
                <div className="mb-4">
                  <label
                    htmlFor="password"
                    className="block mb-1 text-gray-700"
                  >
                    Password
                  </label>
                  <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className={`w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500 ${
                      errors.password ? "border-red-500" : ""
                    }`}
                    placeholder="Enter your password"
                    required
                  />
                  {errors.password && (
                    <p className="text-red-500 text-sm mt-1">
                      {errors.password}
                    </p>
                  )}
                </div>
                <button
                  type="submit"
                  className="w-1/2 bg-green-600 text-white py-2 px-4 rounded-md hover:bg-green-800 transition duration-200"
                >
                  Login
                </button>
              </form>
            )}
            {popupMessage && (
              <Popup
                message={popupMessage}
                onClose={() => setPopupMessage("")}
              />
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Login;
