import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleRegister = () => {
    if (!username || !password) {
      setErrorMessage("Please fill out both fields.");
      return;
    }
    // Store the username and password in localStorage
    localStorage.setItem("username", username);
    localStorage.setItem("password", password);

    // Redirect to login page after registration
    navigate("/login");
  };

  const goBack = () => {
    navigate(-1); // Go back to the previous page
  };

  return (
    <div className="register-container max-w-xs mx-auto my-4 p-4 mt-7 bg-white shadow rounded relative">
      {/* Back Arrow */}
      <button
        onClick={goBack}
        className="absolute top-4 left-4 text-gray-500 hover:text-gray-700"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M15 19l-7-7 7-7"
          />
        </svg>
      </button>

      <h1 className="text-2xl font-bold mb-4 text-center">Register</h1>
      <input
        type="text"
        placeholder="Username"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <div className="flex justify-center">
        <button
          onClick={handleRegister}
          className="w-1/2 bg-blue-700 text-white p-2 rounded-full mt-2"
        >
          Register
        </button>
      </div>

      {errorMessage && (
        <p className="text-red-500 text-sm text-center mt-2">{errorMessage}</p>
      )}
    </div>
  );
}

export default Register;
