import React, { useState } from "react";
import { validateLoginForm, validateRegistrationForm } from "./validation";
import { useNavigate } from "react-router-dom";
import Popup from "./popupComponent";
import { useDispatch } from "react-redux";
import { login } from "../features/auth/authSlice";
import logo from "./images/gameImg.jpg";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [registerEmail, setRegisterEmail] = useState("");
  const [registerPassword, setRegisterPassword] = useState("");
  const [registerName, setRegisterName] = useState("");
  const [registerAddress, setRegisterAddress] = useState("");
  const [registerContactNumber, setRegisterContactNumber] = useState("");
  const [forgotPasswordEmail, setForgotPasswordEmail] = useState("");
  const [errors, setErrors] = useState({});
  const [activeForm, setActiveForm] = useState("login");
  const [popupMessage, setPopupMessage] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const switchToForm = (formName) => {
    setActiveForm(formName);
    setErrors({});
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const validationErrors = validateLoginForm(email, password);
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (response.ok) {
        const { token, user } = data;
        dispatch(login({ token, role: user.role }));
        localStorage.setItem("authToken", token);
        localStorage.setItem("user", user.role);
        navigate("/game");
        setPopupMessage("User logged in successfully");
      } else {
        setErrors({ email: "Invalid email or password" });
      }
    } catch (error) {
      setErrors({ email: "Server error" });
    } finally {
      setTimeout(() => setPopupMessage(""), 3000);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const validationErrors = validateRegistrationForm(
      registerEmail,
      registerPassword,
      registerName,
      registerAddress,
      registerContactNumber
    );
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: registerName,
          email: registerEmail,
          password: registerPassword,
          address: registerAddress,
          contact: registerContactNumber,
        }),
      });

      const data = await response.json();
      if (response.status === 201) {
        setPopupMessage("User registered successfully");
        setTimeout(() => {
          setPopupMessage("");
          switchToForm("login");
        }, 3000);
      } else {
        setErrors({ registerEmail: data.message });
      }
    } catch (error) {
      setErrors({ registerEmail: "Server error" });
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    setPopupMessage("Password reset link sent to your email");
    setTimeout(() => {
      setPopupMessage("");
      switchToForm("login");
    }, 3000);
  };

  return (
    <div className="flex flex-col md:flex-row items-center justify-center min-h-screen bg-gradient-to-r from-blue-400 via-purple-500 to-red-400">
      <div className="w-full md:w-1/2 flex justify-center md:justify-end">
        <img
          src={logo}
          alt="Logo"
          className="hidden md:block max-h-full max-w-full object-contain rounded-3xl shadow-xl"
        />
      </div>
      <div className="w-full md:w-1/2 p-8 flex justify-center">
        <div className="w-full max-w-sm">
          {activeForm === "login" && (
            <form
              onSubmit={handleLogin}
              className="bg-white shadow-lg rounded-xl p-8 bg-gradient-to-r from-purple-300 to-red-400"
            >
              <h2 className="text-2xl font-semibold mb-4 text-gray-800">
                Login
              </h2>
              <div className="mb-4">
                <label htmlFor="email" className="block mb-1 text-gray-700">
                  Email
                </label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className={`w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.email ? "border-red-500" : ""
                  }`}
                  placeholder="Enter your email"
                  required
                />
                {errors.email && (
                  <p className="text-red-500 text-sm mt-1">{errors.email}</p>
                )}
              </div>
              <div className="mb-4">
                <label htmlFor="password" className="block mb-1 text-gray-700">
                  Password
                </label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className={`w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.password ? "border-red-500" : ""
                  }`}
                  placeholder="Enter your password"
                  required
                />
                {errors.password && (
                  <p className="text-red-500 text-sm mt-1">{errors.password}</p>
                )}
              </div>
              <button
                type="submit"
                className="w-full bg-red-500 text-white py-2 rounded-md hover:bg-red-700 transition duration-200"
              >
                Login
              </button>
              <p className="text-center mt-4 text-sm text-gray-600">
                <span
                  className="cursor-pointer text-blue-600"
                  onClick={() => switchToForm("register")}
                >
                  Register
                </span>{" "}
                |
                <span
                  className="cursor-pointer text-blue-600"
                  onClick={() => switchToForm("forgot-password")}
                >
                  Forgot Password?
                </span>
              </p>
            </form>
          )}
          {activeForm === "register" && (
            <form
              onSubmit={handleRegister}
              className="bg-white shadow-lg rounded-xl p-8 bg-gradient-to-r from-purple-300 to-red-400"
            >
              <h2 className="text-2xl font-semibold mb-4 text-gray-800">
                Register
              </h2>
              {/* Similar fields for name, email, password, address, contact */}
              <button
                type="submit"
                className="w-full bg-red-600 text-white py-2 rounded-md hover:bg-red-700 transition duration-200"
              >
                Register
              </button>
              <p className="text-center mt-4 text-lg text-gray-600">
                <span
                  className="cursor-pointer text-blue-900"
                  onClick={() => switchToForm("login")}
                >
                  Back to Login
                </span>
              </p>
            </form>
          )}
          {activeForm === "forgot-password" && (
            <form
              onSubmit={handleForgotPassword}
              className="bg-white shadow-lg rounded-xl p-8 bg-gradient-to-r from-purple-300 to-red-400"
            >
              <h2 className="text-2xl font-semibold mb-4 text-gray-800">
                Forgot Password
              </h2>
              <div className="mb-4">
                <label
                  htmlFor="forgot-password-email"
                  className="block mb-1 text-gray-700"
                >
                  Email
                </label>
                <input
                  type="email"
                  id="forgot-password-email"
                  value={forgotPasswordEmail}
                  onChange={(e) => setForgotPasswordEmail(e.target.value)}
                  className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter your email"
                  required
                />
              </div>
              <button
                type="submit"
                className="w-full bg-red-600 text-white py-2 rounded-md hover:bg-red-700 transition duration-200"
              >
                Submit
              </button>
              <p className="text-center mt-4 text-lg text-gray-600">
                <span
                  className="cursor-pointer text-blue-900"
                  onClick={() => switchToForm("login")}
                >
                  Back to Login
                </span>
              </p>
            </form>
          )}
        </div>
      </div>
      {popupMessage && <Popup message={popupMessage} />}
    </div>
  );
};

export default Login;
