import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Game() {
  const [score, setScore] = useState(
    parseInt(localStorage.getItem("score")) || 0
  );
  const [steps, setSteps] = useState(0);
  const [gameData, setGameData] = useState(null);
  const [gameOver, setGameOver] = useState(false);
  const [message, setMessage] = useState("");
  const [userInput, setUserInput] = useState("");
  const [clickedNumbers, setClickedNumbers] = useState([]);
  const [lives, setLives] = useState(5);
  const navigate = useNavigate();

  useEffect(() => {
    fetchGame();
  }, []);

  const fetchGame = async () => {
    try {
      const response = await fetch(
        "http://localhost:8080/bananagame-1.0-SNAPSHOT/api/game"
      );
      const data = await response.json();
      setGameData(data);
      setMessage("");
    } catch (error) {
      console.error("Error fetching game data:", error);
    }
  };

  const handleInputChange = (event) => {
    setUserInput(event.target.value);
  };

  const handleAnswer = () => {
    const selectedNumber = parseInt(userInput);
    if (isNaN(selectedNumber) || selectedNumber < 0 || selectedNumber > 9) {
      setMessage("Please enter a valid number between 0 and 9.");
      return;
    }

    setSteps(steps + 1);
    setClickedNumbers([...clickedNumbers, selectedNumber]);

    if (selectedNumber === gameData.solution) {
      const newScore = score + 10;
      setScore(newScore);
      localStorage.setItem("score", newScore); // Update score in local storage
      setMessage(
        `Correct! The answer is ${gameData.solution}. You answered in ${
          steps + 1
        } steps.`
      );
      setGameOver(true);
    } else {
      const newLives = lives - 1;
      setLives(newLives);
      if (newLives === 0) {
        setMessage("Game over! You've run out of lives.");
        setGameOver(true);
      } else {
        setMessage("Wrong answer. Try again!");
      }
    }

    setUserInput("");
  };

  const playAgain = () => {
    setSteps(0);
    setGameOver(false);
    setMessage("");
    setUserInput("");
    setClickedNumbers([]);
    setLives(5); // Reset lives
    fetchGame();
  };

  const handleLogout = () => {
    localStorage.removeItem("username");
    localStorage.removeItem("password");
    navigate("/login");
  };

  return (
    <>
      <h1 className="font-bold text-3xl text-green-700 text-center mt-7">
        Banana Game
      </h1>
      <div className="game-container flex flex-col items-center p-6 bg-gray-50 shadow-xl rounded-lg max-w-lg mx-auto mt-10 relative">
        {gameData && (
          <>
            <h1 className="text-3xl font-semibold mb-6 text-center text-indigo-600">
              Welcome, {localStorage.getItem("username")}!
            </h1>

            <button
              onClick={handleLogout}
              className="absolute top-6 right-6 bg-red-500 text-white p-2 rounded-lg shadow-md hover:bg-red-600 transition duration-300"
            >
              Logout
            </button>

            <div className="w-full h-64 mb-6 rounded-lg overflow-hidden shadow-lg">
              <img
                src={`data:image/png;base64,${gameData.imageBase64}`}
                alt="Banana game puzzle"
                className="w-full h-full object-cover rounded-lg"
              />
            </div>

            {/* Display Score and Lives */}
            <div className="flex justify-between w-full px-4 mb-4">
              <h2 className="text-2xl font-semibold text-center">
                Score: {score}
              </h2>
              <h2 className="text-2xl font-semibold text-center">
                Lives: {lives}
              </h2>
            </div>

            {message && (
              <div className="toast-message text-center mb-4 p-2 bg-indigo-600 text-white rounded-lg shadow-md">
                <p>{message}</p>
              </div>
            )}

            {!gameOver && (
              <div className="mb-4 w-full flex flex-col items-center">
                <input
                  type="number"
                  value={userInput}
                  onChange={handleInputChange}
                  className="p-3 rounded-lg border-2 border-blue-500 mb-4 text-center w-full"
                  placeholder="Enter number"
                  min="0"
                  max="9"
                />
                <button
                  onClick={handleAnswer}
                  className="bg-blue-500 text-white p-4 rounded-lg shadow-md hover:bg-blue-600 transition duration-300"
                >
                  Submit Answer
                </button>
              </div>
            )}

            {gameOver && (
              <div className="text-center mb-6">
                <p className="text-lg text-green-600 mb-4">
                  {lives > 0 ? (
                    <>
                      You answered correctly in {steps} steps! The correct
                      answer was {gameData.solution}.
                    </>
                  ) : (
                    <>Game Over! The correct answer was {gameData.solution}.</>
                  )}
                </p>
                <button
                  onClick={playAgain}
                  className="bg-green-500 text-white p-4 rounded-lg shadow-md hover:bg-green-600 transition duration-300"
                >
                  Play Again
                </button>
              </div>
            )}

            <div className="mt-6">
              <h2 className="text-xl font-semibold">Numbers You Clicked:</h2>
              <div className="flex justify-center mt-2">
                {clickedNumbers.length > 0 ? (
                  clickedNumbers.map((num, index) => (
                    <span
                      key={index}
                      className="bg-blue-300 text-white p-2 rounded-lg mx-2"
                    >
                      {num}
                    </span>
                  ))
                ) : (
                  <p>No numbers clicked yet.</p>
                )}
              </div>
            </div>
          </>
        )}
      </div>
    </>
  );
}

export default Game;
