import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { logout } from "../features/auth/authSlice";

function Game() {
  const [score, setScore] = useState(
    parseInt(localStorage.getItem("score")) || 0
  );
  const dispatch = useDispatch();
  const [steps, setSteps] = useState(0);
  const [gameData, setGameData] = useState(null);
  const [gameOver, setGameOver] = useState(false);
  const [message, setMessage] = useState("");
  const [userInput, setUserInput] = useState("");
  const [clickedNumbers, setClickedNumbers] = useState([]);
  const [lives, setLives] = useState(5);
  const [profile, setProfile] = useState({});
  const [profileVisible, setProfileVisible] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchGame();
    fetchProfile(); // Fetch profile data
  }, []);

  // Function to fetch game data
  const fetchGame = async () => {
    try {
      const response = await fetch(
        "http://localhost:8080/bananagame-1.0-SNAPSHOT/api/game"
      );
      const data = await response.json();
      setGameData(data);
      setMessage("");
    } catch (error) {
      console.error("Error fetching game data:", error);
    }
  };

  // Function to fetch profile data
  const fetchProfile = async () => {
    const token = localStorage.getItem("authToken");
    try {
      const response = await fetch("http://localhost:5000/api/profile", {
        headers: {
          Authorization: ` ${token}`,
        },
      });
      const data = await response.json();
      setProfile(data.profile);
    } catch (error) {
      console.error("Error fetching profile data:", error);
    }
  };

  const handleInputChange = (event) => {
    setUserInput(event.target.value);
  };

  const handleAnswer = () => {
    const selectedNumber = parseInt(userInput);
    if (isNaN(selectedNumber) || selectedNumber < 0 || selectedNumber > 9) {
      setMessage("Please enter a valid number between 0 and 9.");
      return;
    }

    setSteps(steps + 1);
    setClickedNumbers([...clickedNumbers, selectedNumber]);

    if (selectedNumber === gameData.solution) {
      const newScore = score + 10;
      setScore(newScore);
      localStorage.setItem("score", newScore);
      setMessage(`Correct! You answered in ${steps + 1} steps.`);
      setGameOver(true);
    } else {
      const newLives = lives - 1;
      setLives(newLives);
      if (newLives === 0) {
        setMessage("Game over! You've run out of lives.");
        setGameOver(true);
      } else {
        setMessage("Wrong answer. Try again!");
      }
    }

    setUserInput("");
  };

  const playAgain = () => {
    setSteps(0);
    setGameOver(false);
    setMessage("");
    setUserInput("");
    setClickedNumbers([]);
    setLives(5);
    fetchGame();
  };

  const handleLogout = () => {
    dispatch(logout());
    navigate("/login");
  };

  return (
    <div className="flex flex-col lg:flex-row h-screen">
      {/* Profile Section */}
      <div className="lg:w-1/3 w-full p-6 bg-gray-100 rounded-lg shadow-md flex items-center justify-center">
        <div className="w-full text-center">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">
            {profile?.name || "User's Profile"}
          </h2>
          <div className="mb-4">
            <button
              onClick={() => setProfileVisible(!profileVisible)}
              className="bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 transition duration-300"
            >
              {profileVisible ? "Hide Profile" : "Show Profile"}
            </button>
          </div>

          {profileVisible && (
            <div className="bg-white p-4 rounded-lg shadow-md">
              <p className="text-sm text-gray-600">
                <strong>Email:</strong> {profile?.email || "N/A"}
              </p>
              <p className="text-sm text-gray-600">
                <strong>Bio:</strong> {profile?.bio || "No bio available."}
              </p>
              <p className="text-sm text-gray-600">
                <strong>Contact:</strong> {profile?.contact || "N/A"}
              </p>
              <p className="text-sm text-gray-600">
                <strong>Address:</strong> {profile?.address || "N/A"}
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Game Controls Section */}
      <div className="lg:w-2/3 w-full h-full flex flex-col items-center p-6 bg-white shadow-xl overflow-y-auto">
        <h1 className="text-3xl font-semibold mb-4 text-green-600 text-center">
          Welcome, {localStorage.getItem("username")}!
        </h1>

        <button
          onClick={handleLogout}
          className="absolute top-4 right-3 bg-red-600 text-white p-2 rounded-lg shadow-md hover:bg-red-900 transition duration-300"
        >
          Logout
        </button>

        <div className="w-full px-4 mb-4 flex justify-between">
          <h2 className="text-xl font-bold text-yellow-600">Score: {score}</h2>
          <h2 className="text-xl font-bold text-yellow-600">Lives: {lives}</h2>
        </div>

        {message && (
          <div className="text-center mb-4 p-2 bg-green-600 font-mono text-white rounded-lg shadow-md">
            <p>{message}</p>
          </div>
        )}

        {!gameOver && (
          <div className="mb-4 w-full flex flex-col items-center">
            <input
              type="number"
              value={userInput}
              onChange={handleInputChange}
              className="p-3 rounded-lg border-2 border-green-500 mb-4 text-center w-full"
              placeholder="Enter number"
              min="0"
              max="9"
            />
            <button
              onClick={handleAnswer}
              className="bg-green-600 text-white p-4 rounded-lg shadow-md hover:bg-green-800 transition duration-300"
            >
              Submit Answer
            </button>
          </div>
        )}

        {gameOver && (
          <div className="text-center mb-6">
            <p className="text-lg text-green-600 mb-4">
              {lives > 0 ? (
                <>You answered correctly in {steps} steps!</>
              ) : (
                <>Game Over! The correct answer was {gameData.solution}.</>
              )}
            </p>
            <button
              onClick={playAgain}
              className="bg-green-600 text-white p-4 rounded-lg shadow-md hover:bg-green-800 transition duration-300"
            >
              Play Again
            </button>
          </div>
        )}

        <div className="mt-6 w-full">
          <h2 className="text-xl font-semibold text-center">
            Numbers You Clicked:
          </h2>
          <div className="flex justify-center mt-2 flex-wrap">
            {clickedNumbers.length > 0 ? (
              clickedNumbers.map((num, index) => (
                <span
                  key={index}
                  className=" text-yellow-600 p-2  text-2xl mx-2 mb-2"
                >
                  {num}
                </span>
              ))
            ) : (
              <p>No numbers clicked yet.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Game;
