import React, { useState } from "react";

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleLogin = () => {
    const validUsername = "testuser";
    const validPassword = "password";

    if (username === validUsername && password === validPassword) {
      document.cookie = `username=${username}; path=/; max-age=3600`; // Store in cookie
      onLogin(username); // Pass username to App component
    } else {
      setErrorMessage("Login failed. Please try again.");
    }
  };

  return (
    <div className="login-container max-w-xs mx-auto my-4 p-4 bg-white shadow rounded">
      <h1 className="text-2xl font-bold mb-4">Login to Play</h1>
      <input
        type="text"
        placeholder="Username"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button
        onClick={handleLogin}
        className="w-full bg-blue-500 text-white p-2 rounded mt-2"
      >
        Login
      </button>
      {errorMessage && <p className="text-red-500 text-sm">{errorMessage}</p>}
    </div>
  );
}

export default Login;
