import React, { useState, useEffect } from "react";

function Game({ username }) {
  const [score, setScore] = useState(0);
  const [steps, setSteps] = useState(0);
  const [gameData, setGameData] = useState(null);
  const [gameOver, setGameOver] = useState(false);

  // Fetch Game Data
  useEffect(() => {
    fetchGame();
  }, []);

  const fetchGame = async () => {
    try {
      const response = await fetch(
        "http://localhost:8080/bananagame-1.0-SNAPSHOT/api/game"
      );
      const data = await response.json();
      setGameData(data);
    } catch (error) {
      console.error("Error fetching game data:", error);
    }
  };

  const handleAnswer = (selected) => {
    setSteps(steps + 1);
    if (selected === gameData.solution) {
      setScore(score + 1);
      alert(`Correct! Steps taken: ${steps + 1}`);
      setGameOver(true);
    } else {
      alert("Wrong answer. Try again!");
    }
  };

  const playAgain = () => {
    setSteps(0);
    setScore(0);
    setGameOver(false);
    fetchGame();
  };

  return (
    <div className="game-container p-4 bg-white shadow rounded max-w-md mx-auto">
      {gameData && (
        <>
          <h1 className="text-xl font-bold mb-4">Welcome, {username}!</h1>
          <img
            src={`data:image/png;base64,${gameData.imageBase64}`}
            // alt="Game Image"
            className="w-full h-64 object-cover mb-4"
          />
          <div className="flex justify-around mb-4">
            {[...Array(10).keys()].map((num) => (
              <button
                key={num}
                onClick={() => handleAnswer(num)}
                className="bg-blue-500 text-white p-2 rounded"
              >
                {num}
              </button>
            ))}
          </div>
          {gameOver && (
            <div className="text-center">
              <p className="text-lg">
                You answered correctly in {steps} steps!
              </p>
              <button
                onClick={playAgain}
                className="bg-green-500 text-white p-2 rounded mt-4"
              >
                Play Again
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default Game;
