import React from "react";

function UserProfile() {
  // You can replace these with dynamic data from your backend or state
  const userName = localStorage.getItem("username") || "John Doe";
  const userBio = "A passionate gamer and developer!";
  const userContact = "john.doe@example.com";

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold text-center mb-4">User Profile</h2>
      <div className="flex flex-col items-center">
        {/* User Info */}
        <div className="text-center mb-4">
          <img
            src="https://via.placeholder.com/150" // Placeholder image, replace with actual user's image if available
            alt="User Avatar"
            className="w-32 h-32 rounded-full mb-4"
          />
          <h3 className="text-xl font-medium">{userName}</h3>
          <p className="text-gray-600">{userBio}</p>
          <p className="text-gray-500">{userContact}</p>
        </div>

        {/* Edit Profile Button */}
        <button className="mt-4 py-2 px-6 bg-blue-600 text-white rounded-lg hover:bg-blue-800 transition duration-300">
          Edit Profile
        </button>
      </div>
    </div>
  );
}

export default UserProfile;
