import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    const storedUsername = localStorage.getItem("username");
    const storedPassword = localStorage.getItem("password");

    if (username === storedUsername && password === storedPassword) {
      // Successful login
      navigate("/game"); // Redirect to home or game page (if you have one)
    } else {
      setErrorMessage("Invalid credentials, please try again.");
    }
  };

  // Navigate to the registration page
  const navigateToRegister = () => {
    navigate("/register");
  };

  return (
    <div className="login-container max-w-xs mx-auto my-4  bg-white shadow rounded-2xl p-7 mt-7">
      <h1 className="text-2xl font-bold mb-4">Login to Play</h1>
      <input
        type="text"
        placeholder="Username"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button
        onClick={handleLogin}
        className="w-1/2 text-center bg-blue-700 text-white p-2 rounded-full mt-2"
      >
        Login
      </button>

      {/* Display error message if login fails */}
      {errorMessage && <p className="text-red-500 text-sm">{errorMessage}</p>}

      {/* Button to navigate to the Register page */}
      <button
        onClick={navigateToRegister}
        className="w-full bg-white text-black p-2 rounded mt-4"
      >
        Don't have an account? Register
      </button>
    </div>
  );
}

export default Login;
