import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  const handleRegister = () => {
    if (!username || !password) {
      setErrorMessage("Please fill out both fields.");
      return;
    }

    // Store the username and password in localStorage
    localStorage.setItem("username", username);
    localStorage.setItem("password", password);

    // Display success message
    setSuccessMessage("Registration successful! Redirecting to login...");
    setErrorMessage(""); // Clear any previous error message

    // Redirect to login page after a short delay
    setTimeout(() => {
      navigate("/login");
    }, 2000);
  };

  const goBack = () => {
    navigate(-1); // Go back to the previous page
  };

  return (
    <div className="register-container max-w-md mx-auto my-10 p-8 bg-white shadow-lg rounded-xl border-4 border-green-500 relative">
      <div className="flex items-center mb-4">
        {/* Back Arrow inside the container */}
        <button
          onClick={goBack}
          className="text-green-500 hover:text-green-700 mr-2"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M15 19l-7-7 7-7"
            />
          </svg>
        </button>
        <h1 className="text-3xl font-semibold text-green-800 text-center flex-1">
          Create an Account
        </h1>
      </div>
      <p className="text-green-500 mb-6 text-center">
        Fill out the form below to register
      </p>
      <input
        type="text"
        placeholder="Username"
        className="input w-full p-3 mb-4 border border-green-300 rounded-lg focus:border-green-600 focus:outline-none"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="input w-full p-3 mb-4 border border-green-300 rounded-lg focus:border-green-600 focus:outline-none"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <div className="flex justify-center">
        <button
          onClick={handleRegister}
          className="w-full bg-green-500 text-white p-3 rounded-lg hover:bg-green-600 transition duration-300"
        >
          Register
        </button>
      </div>

      {successMessage && (
        <p className="text-green-600 text-sm mt-4 text-center">
          {successMessage}
        </p>
      )}

      {errorMessage && (
        <p className="text-red-500 text-sm mt-4 text-center">{errorMessage}</p>
      )}
    </div>
  );
}

export default Register;
