import React, { useState } from "react";

function Register({ onRegister }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleRegister = () => {
    if (username && password) {
      // Save username and password to localStorage
      localStorage.setItem("username", username);
      localStorage.setItem("password", password);
      setErrorMessage("");
      onRegister(username); // Trigger the onRegister callback (for example, to update the UI)
    } else {
      setErrorMessage("Please fill in both fields.");
    }
  };

  return (
    <div className="register-container max-w-xs mx-auto my-4 p-4 bg-white shadow rounded">
      <h1 className="text-2xl font-bold mb-4">Register</h1>
      <input
        type="text"
        placeholder="Username"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button
        onClick={handleRegister}
        className="w-full bg-green-500 text-white p-2 rounded mt-2"
      >
        Register
      </button>
      {errorMessage && <p className="text-red-500 text-sm">{errorMessage}</p>}
    </div>
  );
}

export default Register;
