import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleRegister = () => {
    if (!username || !password) {
      setErrorMessage("Please fill out both fields.");
      return;
    }
    // Store the username and password in localStorage
    localStorage.setItem("username", username);
    localStorage.setItem("password", password);

    // Redirect to login page after registration
    navigate("/login");
  };

  const goBack = () => {
    navigate(-1); // Go back to the previous page
  };

  return (
    <div className="register-container max-w-md mx-auto my-10 p-8 bg-white shadow-lg rounded-xl border-4 border-green-500">
      {/* Back Arrow */}
      <button
        onClick={goBack}
        className="absolute top-4 left-4 text-gray-500 hover:text-gray-700"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M15 19l-7-7 7-7"
          />
        </svg>
      </button>

      <h1 className="text-3xl font-semibold text-gray-800 mb-6 text-center">
        Create an Account
      </h1>
      <p className="text-gray-500 mb-6 text-center">
        Fill out the form below to register
      </p>
      <input
        type="text"
        placeholder="Username"
        className="input w-full p-3 mb-4 border border-gray-300 rounded-lg focus:border-blue-600 focus:outline-none"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="input w-full p-3 mb-4 border border-gray-300 rounded-lg focus:border-blue-600 focus:outline-none"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <div className="flex justify-center">
        <button
          onClick={handleRegister}
          className="w-full bg-green-500 text-white p-3 rounded-lg hover:bg-green-600 transition duration-300"
        >
          Register
        </button>
      </div>

      {errorMessage && (
        <p className="text-red-500 text-sm mt-4 text-center">{errorMessage}</p>
      )}
    </div>
  );
}

export default Register;
