import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleRegister = () => {
    if (!username || !password) {
      setErrorMessage("Please fill out both fields.");
      return;
    }
    // Store the username and password in localStorage
    localStorage.setItem("username", username);
    localStorage.setItem("password", password);

    // Redirect to login page after registration
    navigate("/login");
  };

  return (
    <div className="register-container max-w-xs mx-auto my-4 p-4 bg-white shadow rounded flex justify-col">
      <h1 className="text-2xl font-bold mb-4">Register</h1>
      <input
        type="text"
        placeholder="Username"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="input w-full p-2 mb-2 border border-gray-300 rounded"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <div className="flex justify-center">
        <button
          onClick={handleRegister}
          className="w-1/2 bg-blue-700 text-white p-2 rounded-full mt-2"
        >
          Register
        </button>
      </div>
      {errorMessage && <p className="text-red-500 text-sm">{errorMessage}</p>}
    </div>
  );
}

export default Register;
