import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { logout } from "../features/auth/authSlice";
import axios from "axios";

function Game() {
  const [score, setScore] = useState(
    parseInt(localStorage.getItem("score")) || 0
  );
  const dispatch = useDispatch();
  const [steps, setSteps] = useState(0);
  const [gameData, setGameData] = useState(null);
  const [gameOver, setGameOver] = useState(false);
  const [message, setMessage] = useState("");
  const [userInput, setUserInput] = useState("");
  const [clickedNumbers, setClickedNumbers] = useState([]);
  const [lives, setLives] = useState(5);
  const [profile, setProfile] = useState({});
  const [profileSidebarOpen, setProfileSidebarOpen] = useState(false); // state to toggle profile sidebar
  const token = localStorage.getItem("authToken");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/profile", {
          headers: {
            Authorization: ` ${token}`,
          },
        });
        setProfile(response.data.profile);
      } catch (error) {
        console.error("Error fetching profile data:", error);
      }
    };

    fetchProfile();
    fetchGame();
  }, [token]);

  const fetchGame = async () => {
    try {
      const response = await fetch(
        "http://localhost:8080/bananagame-1.0-SNAPSHOT/api/game"
      );
      const data = await response.json();
      setGameData(data);
      setMessage("");
    } catch (error) {
      console.error("Error fetching game data:", error);
    }
  };

  const handleInputChange = (event) => {
    setUserInput(event.target.value);
  };

  const handleAnswer = () => {
    const selectedNumber = parseInt(userInput);
    if (isNaN(selectedNumber) || selectedNumber < 0 || selectedNumber > 9) {
      setMessage("Please enter a valid number between 0 and 9.");
      return;
    }

    setSteps(steps + 1);
    setClickedNumbers([...clickedNumbers, selectedNumber]);

    if (selectedNumber === gameData.solution) {
      const newScore = score + 10;
      setScore(newScore);
      localStorage.setItem("score", newScore);
      setMessage(`Correct! You answered in ${steps + 1} steps.`);
      setGameOver(true);
    } else {
      const newLives = lives - 1;
      setLives(newLives);
      if (newLives === 0) {
        setMessage("Game over! You've run out of lives.");
        setGameOver(true);
      } else {
        setMessage("Wrong answer. Try again!");
      }
    }

    setUserInput("");
  };

  const playAgain = () => {
    setSteps(0);
    setGameOver(false);
    setMessage("");
    setUserInput("");
    setClickedNumbers([]);
    setLives(5);
    fetchGame();
  };

  const handleLogout = () => {
    dispatch(logout());
    navigate("/login");
  };

  const toggleProfileSidebar = () => {
    setProfileSidebarOpen(!profileSidebarOpen);
  };

  return (
    <div className="flex flex-col lg:flex-row h-screen">
      {/* Profile Sidebar */}
      {profileSidebarOpen && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 z-50">
          <div className="w-1/3 h-full bg-white shadow-lg p-6 overflow-y-auto">
            <UserProfile /> {/* Render the UserProfile component */}
            <button
              onClick={toggleProfileSidebar}
              className="absolute top-4 right-4 bg-gray-600 text-white p-2 rounded-full"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Profile Section */}
      <div className="lg:w-1/2 w-full h-full flex items-center justify-center p-6 bg-gray-100 shadow-xl rounded-lg">
        <div className="flex items-center space-x-4">
          {/* Profile image */}
          {profile.profileImage && (
            <img
              src={profile.profileImage}
              alt="Profile"
              className="w-16 h-16 rounded-full object-cover"
            />
          )}
          {/* User name */}
          <h1 className="text-2xl font-semibold text-green-600">
            Welcome, {profile.user?.name || "User"}!
          </h1>
          {/* Button to toggle profile sidebar */}
          <button
            onClick={toggleProfileSidebar}
            className="ml-4 bg-green-600 text-white p-2 rounded-lg shadow-md hover:bg-green-800"
          >
            View Profile
          </button>
        </div>
      </div>

      {/* Game Controls Section */}
      <div className="lg:w-1/2 w-full h-full flex flex-col items-center p-6 bg-white shadow-xl overflow-y-auto">
        <button
          onClick={handleLogout}
          className="absolute top-4 right-3 bg-red-600 text-white p-2 rounded-lg shadow-md hover:bg-red-900 transition duration-300"
        >
          Logout
        </button>

        <div className="w-full px-4 mb-4 flex justify-between">
          <h2 className="text-xl font-bold text-yellow-600">Score: {score}</h2>
          <h2 className="text-xl font-bold text-yellow-600">Lives: {lives}</h2>
        </div>

        {message && (
          <div className="text-center mb-4 p-2 bg-green-600 font-mono text-white rounded-lg shadow-md">
            <p>{message}</p>
          </div>
        )}

        {!gameOver && (
          <div className="mb-4 w-full flex flex-col items-center">
            <input
              type="number"
              value={userInput}
              onChange={handleInputChange}
              className="p-3 rounded-lg border-2 border-green-500 mb-4 text-center w-full"
              placeholder="Enter number"
              min="0"
              max="9"
            />
            <button
              onClick={handleAnswer}
              className="bg-green-600 text-white p-4 rounded-lg shadow-md hover:bg-green-800 transition duration-300"
            >
              Submit Answer
            </button>
          </div>
        )}

        {gameOver && (
          <div className="text-center mb-6">
            <p className="text-lg text-green-600 mb-4">
              {lives > 0 ? (
                <>You answered correctly in {steps} steps!</>
              ) : (
                <>Game Over! The correct answer was {gameData.solution}.</>
              )}
            </p>
            <button
              onClick={playAgain}
              className="bg-green-600 text-white p-4 rounded-lg shadow-md hover:bg-green-800 transition duration-300"
            >
              Play Again
            </button>
          </div>
        )}

        <div className="mt-6 w-full">
          <h2 className="text-xl font-semibold text-center">
            Numbers You Clicked:
          </h2>
          <div className="flex justify-center mt-2 flex-wrap">
            {clickedNumbers.length > 0 ? (
              clickedNumbers.map((num, index) => (
                <span
                  key={index}
                  className="text-yellow-600 p-2 text-2xl mx-2 mb-2"
                >
                  {num}
                </span>
              ))
            ) : (
              <p>No numbers clicked yet.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Game;
