import React, { useState, useEffect } from "react";
import axios from "axios";

const UserProfile = () => {
  const [profile, setProfile] = useState({
    user: {},
    bio: "",
    profileImage: "",
    contact: "",
    address: "",
  });
  const [bio, setBio] = useState("");
  const [profileImage, setProfileImage] = useState(null);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [leaderboard, setLeaderboard] = useState([]); // Store leaderboard data

  const token = localStorage.getItem("authToken");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/profile", {
          headers: {
            Authorization: ` ${token}`,
          },
        });

        setProfile(response.data.profile);
        setBio(response.data.profile.bio || "");
      } catch (err) {
        setError(err.response?.data?.msg || "Error fetching profile");
      }
    };

    const fetchLeaderboard = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/api/game/leaderboard",
          {
            headers: {
              // Authorization: ` ${token}`,
            },
          }
        );
        setLeaderboard(response.data); // Assume API returns an array of top players
      } catch (err) {
        console.error("Error fetching leaderboard:", err);
      }
    };

    fetchProfile();
    fetchLeaderboard();
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("bio", bio);
    if (profileImage) formData.append("profileImage", profileImage);

    try {
      const response = await axios.put(
        "http://localhost:5000/api/profile/update",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: ` ${token}`,
          },
        }
      );

      setProfile((prevProfile) => ({
        ...prevProfile,
        bio: response.data.profile.bio,
        profileImage: response.data.profile.profileImage,
      }));
      setSuccess("Profile updated successfully!");
      setError(null);
      setProfileImage(null);
      setIsEditing(false);
    } catch (err) {
      setError(err.response?.data?.msg || "Error updating profile");
      setSuccess("");
    }
  };

  if (!profile.user.name) return <p className="text-gray-600">Loading...</p>;

  return (
    <div className="max-w-6xl mx-auto p-6 grid grid-cols-1 lg:grid-cols-3 gap-6">
      {/* Profile Section */}
      <div className="col-span-1 bg-gradient-to-r from-green-400 to-green-600 text-white p-6 rounded-lg flex flex-col items-center">
        {profile.profileImage && (
          <img
            src={profile.profileImage}
            alt="Profile"
            className="w-32 h-32 object-cover rounded-full mb-4"
          />
        )}
        <h1 className="text-2xl font-semibold">{profile.user.name}</h1>
        <p className="text-lg mt-2">{profile.bio}</p>
        <button
          onClick={() => setIsEditing(true)}
          className="mt-4 px-4 py-2 bg-green-700 text-white font-semibold rounded-lg hover:bg-green-800 focus:outline-none focus:ring-2 focus:ring-green-500"
        >
          Update Profile
        </button>
      </div>

      {/* Main Content */}
      <div className="col-span-2 bg-white shadow-md rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Information</h2>
        <p className="text-gray-700 mb-2">
          <strong>Email:</strong> {profile.user.email}
        </p>
        <p className="text-gray-700 mb-2">
          <strong>Contact:</strong> {profile.user.contact}
        </p>
        <p className="text-gray-700 mb-2">
          <strong>Address:</strong> {profile.user.address}
        </p>

        {success && <p className="text-green-600 mb-4">{success}</p>}
        {error && <p className="text-red-600 mb-4">{error}</p>}

        {isEditing && (
          <form onSubmit={handleSubmit} className="space-y-4">
            <label className="block">
              <span className="text-gray-700">Bio:</span>
              <textarea
                value={bio}
                onChange={(e) => setBio(e.target.value)}
                className="mt-1 block w-full border border-gray-300 rounded-lg p-2"
                rows="4"
              />
            </label>
            <label className="block">
              <span className="text-gray-700">Profile Image:</span>
              <input
                type="file"
                onChange={(e) => setProfileImage(e.target.files[0])}
                className="mt-1 block w-full border border-gray-300 rounded-lg p-2"
              />
            </label>
            <button
              type="submit"
              className="px-4 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              Update Profile
            </button>
            <button
              type="button"
              onClick={() => setIsEditing(false)}
              className="ml-2 px-4 py-2 bg-gray-500 text-white font-semibold rounded-lg hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500"
            >
              Cancel
            </button>
          </form>
        )}

        {/* Leaderboard */}
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-4">Leaderboard</h2>
          <ul className="space-y-2">
            {leaderboard.length > 0 ? (
              leaderboard.map((player, index) => (
                <li
                  key={index}
                  className="flex justify-between bg-gray-100 p-3 rounded-lg shadow-sm"
                >
                  <span>
                    {index + 1}. {player.name}
                  </span>
                  <span className="font-semibold">{player.totalScore} pts</span>
                </li>
              ))
            ) : (
              <p className="text-gray-500">No leaderboard data available.</p>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
